from flask import Flask, jsonify, requestimport imgDrowimport osfrom flask import Flask, request, redirect, url_forfrom werkzeug.utils import secure_filenameUPLOAD_FOLDER_1 = '/var/www/voiceTest/sounds/files/in'UPLOAD_FOLDER_2 = '/var/www/voiceTest/sounds/files/out'ALLOWED_EXTENSIONS = set(['txt', 'wav', 'pdf', 'png', 'jpg', 'jpeg', 'gif', 'log'])app = Flask(__name__)app.config['UPLOAD_FOLDER_1'] = UPLOAD_FOLDER_1app.config['UPLOAD_FOLDER_2'] = UPLOAD_FOLDER_2languages = [{'name' : 'Python'},{'name' : 'Ruby'} ]@app.route('/', methods=['POST'])def tt():    """    orig = ""    rec = ""        file = request.files[ 'original' ]    if file :        filename = secure_filename(file.filename)                orig = filename         file.save(os.path.join(app.config['UPLOAD_FOLDER_1'], filename))       file = request.files[ 'record' ]    if file :        filename = secure_filename(file.filename)                rec = filename         file.save(os.path.join(app.config['UPLOAD_FOLDER_2'], filename))       orig = 'sounds/files/in/' + orig    rec = 'sounds/files/out/' + rec    """    orig = request.json[ 'original' ]    rec = request.json[ 'record' ]        return jsonify( imgDrow.main(orig,rec) )       @app.route('/lang', methods=['GET'])def returnAll():    return jsonify({ 'languages' : languages })@app.route('/lang/<string:name>', methods=['GET'])def returnOne(name):    langs = [language for language in languages if language['name'] == name]    return jsonify({ name : langs })if __name__ == '__main__':    app.run( debug=True, port=8080)     #app.run( host='0.0.0.0', debug=True, port=8080)